export default {
  name: "BookSearch",
  data: function data() {
    return {
      searchForm: {
        title: "",
        author: "",
        isbn: "",
        categoryId: ""
      }
    };
  },
  methods: {
    onSearch: function onSearch() {
      // 过滤掉空值参数
      var params = {};
      for (var key in this.searchForm) {
        if (this.searchForm[key]) {
          params[key] = this.searchForm[key];
        }
      }
      this.$emit("search", params);
    },
    onReset: function onReset() {
      this.searchForm = {
        title: "",
        author: "",
        isbn: "",
        categoryId: ""
      };
      this.$emit("reset");
    }
  }
};
